package com.example.dodo.revisaofragment;import android.app.FragmentManager;import android.content.Intent;import android.content.res.Configuration;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;public class DetalheActivity extends AppCompatActivity {    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        if(getResources().getConfiguration().orientation                == Configuration.ORIENTATION_LANDSCAPE){            setResult(RESULT_CANCELED);            finish();        }        setContentView(R.layout.activity_detalhe);        String time = getIntent().getStringExtra("time");        FragmentManager fragmentManager = getFragmentManager();        DetalheFrag detalheFrag = (DetalheFrag)fragmentManager.findFragmentById(R.id.DetalheFrag);        detalheFrag.setTxtNomeTime(time);    }    @Override    public void onBackPressed() {        setResult(RESULT_OK);        finish();    }}